import React,{ Component} from 'react';
import './App.css';
import YTSearch from 'youtube-api-search';
import Search from './Search.jsx';
import List from './List.jsx';
import Movie from './Movie.jsx';
import VideoList from './VideoList.jsx';

const API_KEY = 'AIzaSyBHZ96DiIrwOfHPjlpqBkIkCukbt81Lncg';

class App extends Component{
  constructor(props){
    super(props);
    this.videoSearch = this.videoSearch.bind(this);
    this.addToList = this.addToList.bind(this);
    this.addToSearchList = this.addToSearchList.bind(this);
    this.searchYT = this.searchYT.bind(this);

    this.state={
      list:[],
      searchList:[],
      videoList:[],
    }
  }

  videoSearch(searchText){
    let searchList = this.state.searchList; 
    if(searchText === ""){
      this.searchYT(searchList);
    }else{
      searchList = searchList.concat(searchText);
      this.searchYT(searchList);
    } 
  }

  addToList(item){
    let list = this.state.list;
    let exist = false;   
    if (list.length) {
      list.map(l => {
        if (l === item.toLowerCase()) { 
          exist = true;
        }
      })
    }
    if(!exist){
      list = list.concat(Object.assign({}, item, {checked: false}));
      console.log(list, item);
      this.setState({list});
    }
  }

  addToSearchList(item){
    let searchList = this.state.searchList;
    searchList = searchList.concat(item.toLowerCase());
    this.setState({searchList});
  }

  searchYT(searchList){
    let videoList = this.state.videoList;
    searchList.map(term => (
      YTSearch({key : API_KEY, term}, videos => {
      videoList  = videoList.concat(videos);
      this.setState({videoList});
    })));
    videoList = [];
    this.setState({videoList});
  }

  render(){
    return (
      <div className="App">
        <Search videoSearch={this.videoSearch} addToList={this.addToList} addToSearchList={this.addToSearchList}/>
        <List list={this.state.list} />
        <VideoList videos={this.state.videoList}/>
      </div>
    );
  }

}

export default App;
