import React,{ Component} from 'react';

class VideoList extends Component{
    constructor(props){
      super(props);

    }

    render(){
      return (
        <div className="VideoList">
          {this.props.videos.map(video =>(
              console.log(video),
              <VidoeListItem 
              video={video}
              />
          ))}
        </div>
      );
    }

  }

  const VidoeListItem = ({video}) =>{
    let imageUrl = video.snippet.thumbnails.medium.url;
    let title = video.snippet.title;
    let time = video.snippet.publishedAt;
    let videoUrl = "https://www.youtube.com/embed/"+video.id.videoId
    title = title.replace(/&quot;/g, ' ');
    return(
        <div >
            <img src={imageUrl}  alt="" />
            <p>{title}</p>
            <p>{time}</p>
            <iframe width="560" height="315" src={videoUrl} frameborder="0" allow="autoplay; encrypted-media" allowfullscreen></iframe>
        </div>
    );
  }

  export default VideoList;
