import React,{ Component} from 'react';

class Search extends Component{
    constructor(props){
        super(props);
        this.onTextChange = this.onTextChange.bind(this);

        this.state={
            text:""
        }
    }

    handleSubmit(e){
        e.preventDefault();
        this.props.textOnChange(this.state.text);    
    }

    onTextChange(e) {
        this.setState({ text: e.target.value });
    }

    render(){
        return (
            <div className="Search">
                <input id="search" type="textr" placeholder="Enter movie title" value={this.state.text} onChange={this.onTextChange}/>
                <button onClick={this.handleSubmit}>Find the movie</button>
            </div>
        );
    }

}

export default Search;
