import React,{ Component} from 'react';
import './App.css';
import YTSearch from 'youtube-api-search';
import Search from './Search.jsx';
import List from './List.jsx';
import Movie from './Movie.jsx';
import VideoList from './VideoList.jsx';

const API_KEY = 'AIzaSyB7---2wHkTJHZVYwI17vd-Es-_hr11jDc';

class App extends Component{
  constructor(props){
    super(props);
    this.textOnChange = this.textOnChange.bind(this);
    this.addToList = this.addToList.bind(this);
    this.searchYT = this.searchYT.bind(this);

    this.state={
      searchText:"",
      searchList:[],
      videoList:[],
    }
  }

  textOnChange(value){
    this.setState({searchText: value});
    //console.log(this.state.searchText)
    //this.searchYT(value);
  }

  addToList(item){
    let searchList = this.state.searchList;
    let exist = false;   
    if (searchList.length) {
      searchList.map(l => {
        if (l === item.toLocaleLowerCase) { 
          exist = true;
          console.log("true")
        }
      })
    }

    if(!exist){
      searchList = searchList.concat(item);
      this.setState({searchList});
    }
  }

  searchYT(searchList,searchText){
    let term = searchList.concat(searchText);
    term.map(t => (
      YTSearch({key : API_KEY, term}, videos => {
      console.log(videos)
    })));
  }

  render(){
    return (
      <div className="App">
        <Search textOnChange={this.textOnChange} addToList={this.addToList} search={()=> this.searchYT(this.state.searchList, this.state.searchText)}/>
        <List list={this.state.searchList}/>
        <Movie searchText={this.state.searchText}/>
        <VideoList/>
      </div>
    );
  }

}

export default App;
