import React,{ Component} from 'react';

class VideoList extends Component{
    constructor(props){
      super(props);
      this.state = {
        videos: [],
      };
      this.handleClick = this.handleClick.bind(this);
    }

    handleClick(video) {
        //console.log(e)
        let videos = this.state.videos;
        if (videos.length) {
            videos.map(v => {
              if (v === video) {
                  if(v.isToggleOn){
                    v.display = 'none';
                  }else{
                    v.display = 'block';                      
                  }
                  v.isToggleOn = !v.isToggleOn;
              }
            })
        }
        /*this.setState(prevState => ({
          isToggleOn: !prevState.isToggleOn,
          display: prevState.isToggleOn ? 'none': 'block'
        }));*/
    }

    render(){
        let videos = this.state.videos;
        return (
            <div className="VideoList">
            {
                this.props.videos.map(video =>{
                    videos = videos.concat(Object.assign({}, video, {dispaly: "block", isToggleOn: true}));
                    this.setState({videos});
                })}
            {
                this.state.videos.map(video => (
                    console.log(this.state.videos.length),
                    <VidoeListItem 
                        video={video}
                        isToggleOn={this.state.isToggleOn}
                        handleClick={() => this.handleClick(video)}
                />
                ))
            }
            </div>
        );
    }

  }

  const VidoeListItem = ({video, display, isToggleOn, handleClick}) =>{
    let imageUrl = video.snippet.thumbnails.medium.url;
    let title = video.snippet.title;
    let time = video.snippet.publishedAt;
    let videoUrl = "https://www.youtube.com/embed/"+video.id.videoId
    title = title.replace(/&quot;/g, ' ');
    return(
        <div onClick={handleClick}>
            {
                isToggleOn ? [
                <img src={imageUrl}  alt="" />,
                <p>{title}</p>,
                <p>{time}</p>] :
                <iframe width="560" height="315" src={videoUrl} frameborder="0" allow="autoplay; encrypted-media" allowfullscreen style={{display: display}}></iframe>
            }
        </div>
    );
  }

  export default VideoList;
