import React,{ Component} from 'react';

class VideoList extends Component{
    constructor(props){
      super(props);
      this.state = {
        isToggleOn: true,
        dispaly: 'block'
      };
      this.handleClick = this.handleClick.bind(this);
    }

    handleClick() {
        this.setState(prevState => ({
          isToggleOn: !prevState.isToggleOn,
          display: prevState.isToggleOn ? 'none': 'block'
        }));
    }

    render(){
      return (
        <div className="VideoList">
          {this.props.videos.map(video =>(
              console.log(video),
              <VidoeListItem 
              video={video}
              dispaly={this.state.dispaly}
              isToggleOn={this.state.isToggleOn}
              />
          ))}
        </div>
      );
    }

  }

  const VidoeListItem = ({video, dispaly, isToggleOn}) =>{
    let imageUrl = video.snippet.thumbnails.medium.url;
    let title = video.snippet.title;
    let time = video.snippet.publishedAt;
    let videoUrl = "https://www.youtube.com/embed/"+video.id.videoId
    title = title.replace(/&quot;/g, ' ');
    return(
        <div >
            {
                this.state.isToggleOn ? [
                <img src={imageUrl}  alt="" />,
                <p>{title}</p>,
                <p>{time}</p>] :
                <iframe width="560" height="315" src={videoUrl} frameborder="0" allow="autoplay; encrypted-media" allowfullscreen style={{display: this.state.display}}></iframe>
            }
        </div>
    );
  }

  export default VideoList;
