import React,{ Component} from 'react';
import ReactDOM from 'react-dom';

class VideoList extends Component{
    constructor(props){
      super(props);
      this.state = {
        videos: [],
        isToggleOn: true,
        dispaly: 'block',
        clickedVideo: "",
        hiddenVideo: this.props.hiddenList,
      };
      this.handleClick = this.handleClick.bind(this);
      this.hiddenVideo = this.hiddenVideo.bind(this);
    }

    handleClick(e, video) {
        this.setState(prevState => ({
          isToggleOn: !prevState.isToggleOn,
          display: prevState.isToggleOn ? 'none': 'block',
          clickedVideo: e.id.videoId
        }));
    }

    hiddenVideo(e){
        //console.log(e.target.value);
        //e.preventDefault();
        this.props.addToHiddenList(e.target.value);
    }

    render(){
        let exist = false;  
        return (
            <div className="VideoList">

            {
                this.props.videos.map(video => {
                    console.log(this.props.hiddenList)
                    return(
                        <>
                        {this.props.hiddenList.map(h => {console.log("true")                           
                            if(video.id.videoId === h){
                    console.log("true")
                    exist = true;  
                            }
                        })}
                        </>
                        (!exist) ?
                            <VidoeListItem 
                                video={video}
                                handleClick={() => this.handleClick(video)}
                                dispaly={this.state.dispaly}
                                isToggleOn={this.state.isToggleOn}
                                clickedVideo={this.state.clickedVideo}
                                hiddenVideo={this.hiddenVideo}
                            /> : null 
                        );
                })
            }
            </div>
        );
    }

  }

  const VidoeListItem = ({video,  handleClick, display, isToggleOn, clickedVideo, hiddenVideo}) =>{
    let imageUrl = video.snippet.thumbnails.medium.url;
    let title = video.snippet.title;
    let time = video.snippet.publishedAt;
    let videoUrl = "https://www.youtube.com/embed/"+video.id.videoId
    title = title.replace(/&quot;/g, ' ');
    return(
        <div>
            {clickedVideo===video.id.videoId ?[
                isToggleOn ? [
                        <p>====================================================</p>,
                        <button value={video.id.videoId} onClick={hiddenVideo}>Hidden Video</button>,
                        <div onClick={handleClick}>
                            <p>{title}</p>
                            <p>{time}</p>
                        </div>
                    ]:[
                        <p>====================================================</p>,
                        <iframe width="560" height="315" src={videoUrl} frameborder="0" allow="autoplay; encrypted-media" allowfullscreen style={{display: display}}></iframe>,
                        <br/>,<button value={video.id.videoId} onClick={hiddenVideo}>Hidden Video</button>,
                        <div onClick={handleClick}>
                            <p>{title}</p>
                            <p>{time}</p>
                        </div>
                    ]
                ] : [
                    <p>====================================================</p>,
                    <button value={video.id.videoId} onClick={hiddenVideo}>Hidden Video</button>,
                    <div onClick={handleClick}>                    
                        <p>{title}</p>
                        <p>{time}</p>
                    </div>
                ]}
        </div>
    );
  }

  export default VideoList;
